import api from '../services/api'; //Importa o serviço API para fazer requisições

export default {
  props: ['bookToEdit'],
  data() {
    return {
      book: this.bookToEdit || {
        title: '',
        author: '',
        year: null,
        isbn: '',
        image: null,
        editora: '',
        sinopse: ''
      },
      selectedImage: null //Guardar a imagem selecionada 
    };
  },
  watch: {
    bookToEdit: {
      immediate: true,
      handler(newVal) {
        this.book = newVal || {
          title: '',
          author: '',
          year: null,
          isbn: '',
          image: null,
          editora: '',
          sinopse: ''
        }; // Atualiza os dados quando a prop mudar
      }
    }
  },
  methods: {
    handleFileChange(event) {
      // Armazena o arquivo da imagem selecionada
      this.selectedImage = event.target.files[0];
    },
    //Função para enviar o formulário
    handleSubmit() {
      if (this.book._id) {
        api.updateBook(this.book._id, this.book).then(() => {
          this.$emit('book-updated'); //Emite um evento quando o livro é atualizado
        });
      } else {
        api.addBook(this.book).then(() => {
          this.$emit('book-added'); //Emite um evento quando um novo livro é add
        });
      }
    }
  }
};
