v<template>
  <div class="dashboard-app">
    <!-- Navbar -->
    <nav class="navbar">
      <div class="navbar-left">
        <img src="@/assets/logo.png" alt="Logo" class="navbar-logo" />
      </div>
      <div class="navbar-center">
        <div class="search-container">
          <img src="@/assets/loupe.png" alt="Buscar" class="search-icon" />
          <input
            type="text"
            class="navbar-search"
            placeholder="Pesquisar livros..."
          />
          <img src="@/assets/filter.png" alt="Filtrar" class="search-icon" />
        </div>
      </div>
      <div class="navbar-right">
        <a href="">
          <img src="@/assets/crud.png" alt="Crud" class="icon" />
        </a>
        <a href="">
          <img src="@/assets/dashboard.png" alt="Dashboard" class="icon" />
        </a>
        <a href="">
          <img src="@/assets/salvar.png" alt="Salvar" class="icon" />
        </a>
        <a href="">
          <img src="@/assets/notificacao.png" alt="Notificação" class="icon" />
        </a>
        <a href="">
          <img src="@/assets/perfil.png" alt="Perfil" class="icon" />
        </a>
      </div>
    </nav>

    <!-- Dashboard Content -->
    <div class="dashboard">
      <div class="content">
        <!-- Cards da linha superior -->
        <div class="card">
          <h2>Livros mais Emprestados</h2>
          <canvas id="pieChart"></canvas>
        </div>
        <div class="card">
          <h2>Usuários mais Ativos</h2>
          <canvas id="pieChartUsers"></canvas>
        </div>
      </div>

      <!-- Card e botão centralizados abaixo -->
      <div class="bottom-section">
        <div class="card">
          <h2>Histórico de Reservas</h2>
          <canvas id="barChart"></canvas>
        </div>
        <div class="pdf-button">
          <button @click="generatePDF">Gerar PDF</button>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import { Chart } from "chart.js/auto";

export default {
  methods: {
    generatePDF() {
      alert("PDF Gerado!");
    },
    createCharts() {
      // Gráfico de pizza para "Livros mais Emprestados"
      new Chart(document.getElementById("pieChart").getContext("2d"), {
        type: "pie",
        data: {
          labels: ["Vida Fora da Gangue", "O Menino Maluquinho", "O Príncipe"],
          datasets: [
            {
              data: [33.33, 33.33, 33.33],
              backgroundColor: ["#ff6384", "#36a2eb", "#ffcd56"],
            },
          ],
        },
      });

      // Gráfico de barras para "Histórico de Reservas"
      new Chart(document.getElementById("barChart").getContext("2d"), {
        type: "bar",
        data: {
          labels: ["Vida Fora da Gangue", "O Menino Maluquinho", "O Príncipe"],
          datasets: [
            {
              data: [50, 70, 40],
              backgroundColor: ["#ff6384", "#36a2eb", "#4bc0c0"],
            },
          ],
        },
      });

      // Gráfico de pizza para "Usuários mais Ativos"
      new Chart(document.getElementById("pieChartUsers").getContext("2d"), {
        type: "pie",
        data: {
          labels: ["Cleiton1974", "Eduardo777", "Seila674"],
          datasets: [
            {
              data: [40, 35, 25],
              backgroundColor: ["#36a2eb", "#ff6384", "#4bc0c0"],
            },
          ],
        },
      });
    },
  },
  mounted() {
    this.createCharts();
  },
};
</script>

<style scoped>
.dashboard-app {
  font-family: "Arial", sans-serif;
  background-color: #133957;
  color: #fff;
  height: 100vh;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

/* Navbar */
.navbar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #bcd7e5;
  padding: 10px 30px;
  height: 70px;
  border-bottom: 2px solid #007bff;
}
.navbar-left .navbar-logo {
  height: 50px;
}
.navbar-center .search-container {
  display: flex;
  align-items: center;
  background-color: #fff;
  border-radius: 12px;
  padding: 8px 18px;
  width: 400px;
}
.navbar-right .icon {
  width: 35px;
  height: 35px;
  margin-left: 15px;
}

/* Dashboard */
.dashboard {
  padding: 20px;
  flex: 1;
  overflow-y: auto;
}
.content {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  max-width: 1400px;
  margin: 0 auto;
}
.card {
  background-color: #f9f9f9;
  border-radius: 12px;
  padding: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  text-align: center;
  color: #000;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  height: 260px; /* Reduzir altura */
}
.card h2 {
  margin-bottom: 10px; /* Reduzir espaço do título */
  font-size: 1.2em; /* Tamanho de fonte menor */
}
canvas {
  margin: auto;
  max-width: 90%; /* Aumentar a área dos gráficos */
  max-height: 75%; /* Ajustar para gráficos maiores */
}

/* Bottom Section */
.bottom-section {
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 20px;
}
.bottom-section .card {
  width: 80%; /* Diminuir a largura do card */
  margin-bottom: 15px;
}
.pdf-button button {
  background-color: #007bff;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 6px;
  font-size: 16px;
  cursor: pointer;
}
.pdf-button button:hover {
  background-color: #0056b3;
}
</style>
